#flutter热更新
android: #!/bin/bash export PATH=&quot;$PATH:/path/to/flutter/bin&quot; flutter clean flutter pub get flutter build apk &ndash;release if [ ! -d &ldquo;$WORKSPACE/apk&rdquo; ]; then mkdir -p &ldquo;$WORKSPACE/apk&rdquo; fi timestamp=$(date +&quot;%Y%m%d-%H:%M:%S&quot;) mv build/app/outputs/flutter-apk/app-release.apk $WORKSPACE/apk/release-$timestamp.apk echo &ldquo;安卓包地址:~/apk/release-$timestamp.apk&rdquo;
ios: #!/bin/bash export LANG=en_US.UTF-8 export PATH=&quot;$PATH:/Users/user/Documents/flutter/bin&quot; export PATH=&quot;$PATH:/usr/local/bin:/usr/local/sbin&quot; export PATH=&quot;$PATH:/opt/homebrew/bin&quot; flutter clean flutter pub get cd ios pod install fastlane adhoc echo &ldquo;iOS包地址:~/ios/ipa/release-时间戳.ipa&rdquo;
##fastlane部署
default_platform(:ios)
platform :ios do
desc &ldquo;Build an Ad-Hoc distribution&rdquo;
lane :adhoc do
#清理 Fastlane 和 Xcode 缓存 sh &quot;rm -rf ~/Library/Developer/Xcode/DerivedData/*&quot; sh &quot;rm -rf ~/Library/Logs/gym/*&quot; cert # 获取证书 sigh( app_identifier: &quot;com.

